import React from "react";
import { Card, CardBody, CardTitle, CardText } from "reactstrap";
import { FaEnvelope, FaMapMarkedAlt, FaPhone } from "react-icons/fa";

const Mycard = ({ personDetails }) => {
  <Card>
    <CardBody className="text-center">
      <img
        height="150"
        width="150"
        className="rounded-circle img-thumbnail border-danger"
        src={personDetails.picture?.large}
      />
      <CardTitle className="text-primary">
        <h1>
          <span className="pr-2">{personDetails.name?.title}</span>
          <span>{personDetails.name?.first}</span>
          <span>{personDetails.name?.last}</span>
        </h1>
      </CardTitle>
      <CardText>
        <FaMapMarkedAlt />
        {personDetails.location?.city}
        <p>{personDetails.phone}</p>
      </CardText>
    </CardBody>
  </Card>;
};

export default Mycard;
